int cltNetworkUnitStatisticsTable_load(netsnmp_cache *cache, void *vmagic)
{
 
   int ret, i;
   struct cltNetworkUnitStatisticsTable_entry entry;
   struct device_stats *p, stats;
   
   DEBUGMSGTL((DBGID, "Free cltNetworkUnitStatisticsTable Group\n"));  
   cltNetworkUnitStatisticsTable_removeAllEntry();
	   
   ret = device_topology_update(0);
   
   if (HC_IPC_ERROR(ret)) {
	   CLR_VALID();
	   DEBUGMSGTL((DBGID, "Failed to load cltNetworkUnitStatisticsTable Group\n"));
   } else {
	   SET_VALID();
	   DEBUGMSGTL((DBGID, "Loaded cltNetworkUnitStatisticsTable Group\n"));
   }

   HC_IPC_ASSERT(ret);
   
   for (i = 0; i < device_topology_num; i ++){
   	   p = device_stats_search(&device_topology[i].mac);
	   if (p == NULL){
			DEBUGMSGTL((DBGID, "Could not find stats with device_stats_search, use default\n"));			   	
			device_stats_init(&stats, &device_topology[i]);
			p = &stats;
	   }else {
			DEBUGMSGTL((DBGID, "Find stats with device_stats_search (valid=%d)\n", p->valid_link_stats));	
	   }
	   cltNetworkUnitStatisticsTable_setEntry(&entry, &device_topology[i], p);
	   cltNetworkUnitStatisticsTable_createEntry(device_topology[i].mac.octet,
	   						sizeof(ether_addr_t), &entry);
   }
   
   return ret; 
} 

