void cltNetworkUnitStatisticsTable_commit(unsigned int regNo, void *clientargs)
{
	long ret;
	struct cltNetworkUnitStatisticsTable_entry *ptr;
	ether_addr_t mac;
	
	HC_VALID_ASSERT();

// refresh if any
	for (ptr = cltNetworkUnitStatisticsTable_commit_head; ptr != NULL; ptr = ptr->next) {
		if (ptr->cltNetworkUnitStatisticsStatus == CNUTopologyStatus_refresh){
			DEBUGMSGTL((DBGID, "Refresh CltNetworkUnitStatisticsTable\n"));  			
			memcpy(mac.octet, ptr->cltNetworkUnitMACAddress, sizeof(ether_addr_t));
			ret = device_link_stats_update(&mac);
		}
	}
	cltNetworkUnitStatisticsTable_removeAllCommitEntry();
	
	DEBUGMSGTL((DBGID, "Commited cltNetworkUnitStatisticsTable Group\n"));  	
}


