int modEoCCBATCardTable_load(netsnmp_cache *cache, void *vmagic)
{
    long            ret = -1, i;
	sys_info_t sys;
	cable_parameter_t *cab;
	struct modEoCCBATCardTable_entry entry;
	
	modEoCCBATCardTable_removeAllEntry();	
	DEBUGMSGTL((DBGID, "Free modEoCCBATCardTable Group\n"));  
	
	
	ret = ipc_get_clt_cable_param(&cltcabletable);

	if (!HC_IPC_ERROR(ret)){
		ret = ipc_get_sys_info(&sys);
	}
	
    if (HC_IPC_ERROR(ret)) {
		CLR_VALID();
		DEBUGMSGTL((DBGID, "Failed to load modEoCCBATCardTable Group\n"));
    } else {
		SET_VALID();
        DEBUGMSGTL((DBGID, "Loaded modEoCCBATCardTable Group\n"));
    }

	HC_IPC_ASSERT(ret);

	for (i = 0; i < HC_CABLE_NUM; i ++){
		cab = &cltcabletable.clts[i];
		modEoCCBATCardTable_setEntry(&entry, sys.cabmac[i].octet, cab);
		modEoCCBATCardTable_createEntry(i + 1, &entry);		
	}
	
    return ret; 
} 

