int modEoCCNUTable_load(netsnmp_cache *cache, void *vmagic)
{
	int ret, i;
	struct modEoCCNUTable_entry entry;

	DEBUGMSGTL((DBGID, "Free modEoCCNUTable Group\n"));  
	modEoCCNUTable_removeAllEntry();

	
	ret = device_topology_update(0);
	
    if (HC_IPC_ERROR(ret)) {
		CLR_VALID();
		DEBUGMSGTL((DBGID, "Failed to load modEoCCNUTable Group\n"));
    } else {
		SET_VALID();
        DEBUGMSGTL((DBGID, "Loaded modEoCCNUTable Group\n"));
    }

	HC_IPC_ASSERT(ret);

	for (i = 0; i < device_topology_num; i ++){
		modEoCCNUTable_setEntry(&entry, &device_topology[i]);
		modEoCCNUTable_createEntry(device_topology[i].clt, i + 1, &entry);
	}

    return ret; 
} 

