int cltConfigAdministratorTable_load(netsnmp_cache *cache, void *vmagic)
{
    long            ret_value = -1;
	int i;

    ret_value = ipc_get_sys_admin(&cltadmin);

    if (HC_IPC_ERROR(ret_value)) {
		CLR_VALID();
        DEBUGMSGTL((DBGID, "Failed to load cltConfigAdministratorTable\n"));
    } else {
		SET_VALID();    	
        DEBUGMSGTL((DBGID, "Loaded cltConfigAdministratorTable\n"));
    }

	HC_IPC_ASSERT(ret_value);

	for (i = 0; i < cltadmin.count; i ++)
	{
		cltConfigAdministratorTable_createEntry(i + 1, cltadmin.users[i].name, cltadmin.users[i].pwd, cltadmin.users[i].enable);		
	}
	
    return ret_value;   
} 

void cltConfigAdministratorTable_free(netsnmp_cache *cache, void *magic)
{
	DEBUGMSGTL((DBGID, "Free cltConfigAdministratorTable Group\n"));  
	
	cltConfigAdministratorTable_removeAllEntry();
}


void cltConfigAdministratorTable_commit(unsigned int regNo, void *clientargs)
{
    long   ret_value = -1, i;
    struct cltConfigAdministratorTable_entry *ptr;
	
	HC_VALID_ASSERT();

	for (i = 0; i < cltadmin.count; i ++)
	{
		ptr = cltConfigAdministratorTable_getEntry(i + 1);
		if (ptr){
			cltadmin.users[i].enable = (ptr->cltConfigAdminStatus == AdminStatus_enabled) ? 1 : 0;
			safe_strncpy(cltadmin.users[i].old_name, ptr->old_cltConfigAdminUserName, sizeof(cltadmin.users[i].old_name));
			safe_strncpy(cltadmin.users[i].name, ptr->cltConfigAdminUserName, sizeof(cltadmin.users[i].name));
			safe_strncpy(cltadmin.users[i].pwd, ptr->cltConfigAdminPassword, sizeof(cltadmin.users[i].pwd));
		}
	}

	ret_value = ipc_set_sys_admin(&cltadmin);
	
    if (HC_IPC_ERROR(ret_value)) {
        DEBUGMSGTL((DBGID, "Failed to commit cltConfigAdministratorTable\n"));
    } else {
        DEBUGMSGTL((DBGID, "Committed cltConfigAdministratorTable\n"));
    }

	HC_IPC_ASSERT1(ret_value);	
}


