int cltConfigCableTable_load(netsnmp_cache *cache, void *vmagic)
{
    long            ret = -1, i;

	sys_info_t sys;
	cable_parameter_t *cab;
	
	
	ret = ipc_get_clt_cable_param(&cltcabletable);

	if (!HC_IPC_ERROR(ret)){
		ret = ipc_get_sys_info(&sys);
	}
	
    if (HC_IPC_ERROR(ret)) {
		CLR_VALID();
		DEBUGMSGTL((DBGID, "Failed to load cltConfigCableTable Group\n"));
    } else {
		SET_VALID();
        DEBUGMSGTL((DBGID, "Loaded cltConfigCableTable Group\n"));
    }

	HC_IPC_ASSERT(ret);

	for (i = 0; i < HC_CABLE_NUM; i ++){
		cab = &cltcabletable.clts[i];
		cltConfigCableTable_createEntry(i + 1, sys.cabmac[i].octet,
				1, cab->snid, cab->network_refresh_period, 0, 0);		
	}
	
    return ret; 
} 

void cltConfigCableTable_free(netsnmp_cache *cache, void *magic)
{

	cltConfigCableTable_removeAllEntry();
	
	DEBUGMSGTL((DBGID, "Free cltConfigCableTable Group\n"));  

}


void cltConfigCableTable_commit(unsigned int regNo, void *clientargs)
{
	long ret, i;
    struct cltConfigCableTable_entry *ptr;

	HC_VALID_ASSERT();

	// update data from table

	for (i = 0; i < HC_CABLE_NUM; i ++)
	{
		ptr = cltConfigCableTable_getEntry(i + 1);
		if (ptr){
			cltcabletable.clts[i].snid = ptr->cltConfigCableSNID;
			cltcabletable.clts[i].network_refresh_period = ptr->cltConfigCableNetworkRefreshInterval;			
		}
	}

	ret = ipc_set_clt_cable_param(&cltcabletable);	

	if (HC_IPC_ERROR(ret)) {
		DEBUGMSGTL((DBGID, "Failed to commit cltConfigCableTable Group\n"));
	} else {
		DEBUGMSGTL((DBGID, "Commited cltConfigCableTable Group\n"));
	}

	HC_IPC_ASSERT1(ret);	
}



