void cltNetworkUnitTopologyTable_setEntry(
				struct cltNetworkUnitTopologyTable_entry *entry, 
				ipc_cnu_info_t *cnu)
{
	entry->cltNetworkUnitTEI = cnu->tei;
	memcpy(	entry->cltNetworkUnitMACAddress, cnu->mac.octet, sizeof(ether_addr_t));
	entry->cltNetworkUnitMACAddress_len = sizeof(ether_addr_t);
	safe_strncpy(entry->cltNetworkUnitDeviceModel, cnu->alias, sizeof(entry->cltNetworkUnitDeviceModel));
	entry->cltNetworkUnitDeviceModel_len = strlen(entry->cltNetworkUnitDeviceModel);
	entry->cltNetworkUnitOnlineStatus = (cnu->link == CNU_LINK_UP) ? TruthValue_true : TruthValue_false;
	entry->cltNetworkUnitAuthenticationStatus =((cnu->auth == CNU_AUTH_ANONACCEPT) || (cnu->auth == CNU_AUTH_ACCEPT)) ? TruthValue_true : TruthValue_false;

	if ((cnu->device_id < 0) || (cnu->link != CNU_LINK_UP)){
		entry->cltNetworkUnitConfigurationStatus = TruthValue_false;
	}else {
		entry->cltNetworkUnitConfigurationStatus = TruthValue_true;
	}
	entry->cltNetworkUnitTemplateID = cnu->template_id + 1;
	entry->cltNetworkUnitUpRate = cnu->avgtx;
	entry->cltNetworkUnitDownRate = cnu->avgrx;
	entry->cltNetworkUnitOnlineTime = time(NULL) - cnu->online_tm;
	
	if (cnu->auth == CNU_AUTH_ACCEPT){
		entry->cltNetworkUnitAccessStatus = CNUACCESS_white;
	}else if (cnu->auth == CNU_AUTH_DENY){ // TODO
		entry->cltNetworkUnitAccessStatus = CNUACCESS_black;
	}else {
		entry->cltNetworkUnitAccessStatus = CNUACCESS_anonymous;
	}
	entry->cltNetworkUnitRowStatus = CNURowStatus_active;

	entry->cltNetworkUnitHardwareVersion[0] = 0;
	entry->cltNetworkUnitHardwareVersion_len = strlen(entry->cltNetworkUnitHardwareVersion);
	safe_strncpy(entry->cltNetworkUnitSoftwareVersion, cnu->version, sizeof(entry->cltNetworkUnitSoftwareVersion));
	entry->cltNetworkUnitSoftwareVersion_len = strlen(entry->cltNetworkUnitSoftwareVersion);
	entry->cltNetworkUnitCLTIndex = cnu->clt + 1;
}




