int cltNetworkUnitTopologyTable_load(netsnmp_cache *cache, void *vmagic)
{
	int ret, i;
	struct cltNetworkUnitTopologyTable_entry entry;
		
	ret = device_topology_update(0);
	
    if (HC_IPC_ERROR(ret)) {
		CLR_VALID();
		DEBUGMSGTL((DBGID, "Failed to load cltNetworkUnitTopologyTable Group\n"));
    } else {
		SET_VALID();
        DEBUGMSGTL((DBGID, "Loaded cltNetworkUnitTopologyTable Group\n"));
    }

	HC_IPC_ASSERT(ret);

	for (i = 0; i < device_topology_num; i ++){
		cltNetworkUnitTopologyTable_setEntry(&entry, &device_topology[i]);
		cltNetworkUnitTopologyTable_createEntry(i + 1, &entry);
	}

    return ret; 
} 

